class Scroll{constructor(){this.initScroll()}initScroll(){const e=this,t=(e.elements=document.querySelectorAll(".animate"),e.elementRight=document.querySelectorAll(".animate-right"),e.elementLeft=document.querySelectorAll(".animate-left"),window.innerHeight);window.addEventListener("scroll",()=>{e.elements.forEach(e=>{e.getBoundingClientRect().top<t&&e.classList.add("fade-in")}),e.elementRight.forEach(e=>{e.getBoundingClientRect().top<t-300&&e.classList.add("fade-in-right")}),e.elementLeft.forEach(e=>{e.getBoundingClientRect().top<t-300&&e.classList.add("fade-in-left")})})}}export default Scroll;